mapper.select:
select bi_seq, category_name, bi_name, bi_sub, bi_writer, bi_cop, bi_status, bi_stock, bi_point, bi_reg_dt, bi_mod_dt from 
(select a.*, b.cate_name as category_name from book_info a left outer join category_info b
on a.bi_ci_seq = b.cate_seq ) c

    where category_name like #{keyword}


    where bi_name like #{keyword}

order by bi_seq desc limit 10 offset #{offset}

select count(*) from (select a.*, b.cate_name as categoty_name from book_info a left outer join category_info b
on a.bi_ci_seq = b.cate_seq ) c

    where category_name like #{keyword}


    where bi_name like #{keyword}

mapper.update:
update book_info
set bi_name=#{di_name}, bi_sub=#{bi_sub}, bi_cop=#{bi_cop}, bi_cate=#{bi_cate}, bi_status=#{bi_status}, bi_stock=#{bi_stock},
bi_pub_dt=#{bi_pub_dt}, bi_writer=#{bi_writer}, bi_point=#{bi_point} bi_mod_dt=NOW() where bi_seq=#{bi_seq}
